    private void btnCalcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcActionPerformed
        // TODO add your handling code here:
        int a = Integer.parseInt(txt1.getValue().toString());
        int b = Integer.parseInt(txt2.getValue().toString());
        int c = Integer.parseInt(txt3.getValue().toString());
        int eq = (int)(Math.pow(b, 2)-4*a*c);
        lblResult.setText(Integer.toString(eq));
        if (eq>=0){
            lblResp.setText("Existe Raizes Reais");
        } 
        else{
            lblResp.setText("N√£o Existe Raizes Reais");
        }
    }//GEN-LAST:event_btnCalcActionPerformed

